#!/usr/bin/env python3

#input sequence into dna variable
dna = ''

#convert all letters to uppercase
dna = dna.upper()

#calculate count each ATGC
As = dna.count('A')
Ts = dna.count('T')
Cs = dna.count('C')
Gs = dna.count('G')
print(f"There are {As} A's, {Ts} T's, {Cs} C's, and {Gs} G's")

#calculate total length of string
totalnt=len(dna)
print(f"Total length of sequence is {totalnt}")

#calculate AT content
ATcontent = (As + Ts) / totalnt
GCcontent = (Gs + Cs) / totalnt
print(f"AT content is {ATcontent:.2%}.")
print(f"GC content is {GCcontent:.2%}.")

