#!/usr/bin/env python3

nt_count = {}
#make a set using a DNA sequence
dna = ''
uniq_nt = set(dna)
print("unique nt:", uniq_nt)

#iterate through each nt found in sequence
for nt in uniq_nt:
  #count how many nt is in the dna seq
  count=dna.count(nt)
  #put that nt and count in the dictionary
  nt_count[nt] = count
print("nt_count:", nt_count)

#calculate GC content
print(f'GC content is {(nt_count['G'] + nt_count['C'])/len(dna):.2%}')

